import numpy as np
import pandas as pd
import streamlit as st

# Sayfa Ayarları
st.set_page_config(
    page_title="Which Rice You Are Looking For",
    page_icon="https://plasticseurope.org/wp-content/uploads/2021/10/5.6._aaheader.png",
    layout="wide",
    menu_items={
        "Get help": 'mailto:riceclub@gmail.com',
        "About": "For More Information\n" + "https://github.com/Biotechers/Rice"
    }
)

# Başlık Ekleme
st.title("Rice Classification Project")

# Markdown Oluşturma
st.markdown("Depending on various features of rice agriculture , a research has been made to be able to differentiate two different types of rice **:green[Cammeo]** or **:red[Osmancik]** .")

# Resim Ekleme
st.image("data:image/jpeg;base64,")

st.markdown("After the latest developments in the artificial intelligence industry, they expect us to develop a **machine learning model** in line with their needs and help them with their research.")
st.markdown("In addition, it can be sometimes hard to distinguish between quite similar agricultural products, that's when we came up with this new algorithm that was in need")
st.markdown("*Let's try to help them!*")

st.image("https://cdnimg.webstaurantstore.com/images/guides/658/rice-header-md.jpg")

# Header Ekleme
st.header("Data Dictionary")

st.markdown("- **Class**: Which type of Rice?")
st.markdown("- **Area**: Which area we're looking for")
st.markdown("- **Convex**: What specific angle of convex it has")
st.markdown("- **Major_axis_length**: It's major axis length")
st.markdown("- **Perimeter**: What specific perimeter the plant has")

# Pandasla veri setini okuyalım
df = pd.read_csv("Rice_Cammeo_Osmancik.csv")
#tanımlandı

st.table(df.sample(10, random_state=42))

# Sidebarda Markdown Oluşturma
st.sidebar.markdown("**Choose** the features below to see the result!")

# Sidebarda Kullanıcıdan Girdileri Alma
name = st.sidebar.text_input("Name", help="Please capitalize the first letter of your name!")
surname = st.sidebar.text_input("Surname", help="Please capitalize the first letter of your surname!")
area = st.sidebar.slider("Target Area", min_value=7550, max_value=18913)
convex = st.sidebar.slider("Target Convex Angle", min_value=7723, max_value=19099)
major_axis_length = st.sidebar.slider("Target Major Axis Length", min_value=14526447, max_value=23901050)
perimeter = st.sidebar.slider("Target Perimeter", min_value=35910001, max_value=54844599)

from joblib import load

logreg_model = load('KNN_model.pkl')

input_df = pd.DataFrame({
    'Area': [area],
    'Convex':[convex],
    'Major_axis_length':[major_axis_length],
    'Perimeter':[perimeter]
})

pred = logreg_model.predict(input_df.values)
pred_probability = np.round(logreg_model.predict_proba(input_df.values),4)

if st.sidebar.button("Submit"):

    st.info("Find the result below.")

    from datetime import date, datetime

    today = date.today()
    time = datetime.now().strftime("%H:%M:%S")

    results_df = pd.DataFrame({
    'Name': [name],
    'Surname': [surname],
    'Date': [today],
    'Time': [time],
    'Area': [area],
    'Convex':[convex],
    'Major_axis_length':[major_axis_length],
    'Perimeter':[perimeter],
    'Prediction': [pred],
    'Cammeo Probability': [pred_probability[:,:1]],
    'Osmancik Probability': [pred_probability[:,1:]]
    })

    results_df["Prediction"] = results_df["Prediction"].apply(lambda x: str(x).replace("0","Cammeo"))
    results_df["Prediction"] = results_df["Prediction"].apply(lambda x: str(x).replace("1","Osmancik"))

    st.table(results_df)

    if pred == 0:
        st.image("https://st1.myideasoft.com/idea/ft/48/myassets/products/213/kameo-1000.jpg?revision=1583406604")
    else:
        st.image("https://productimages.hepsiburada.net/s/22/1500/9952480362546.jpg")

else:
    st.markdown("Please click the *Submit Button*!")
        
